/**
 * Reducers for Navigation table
 */
import bundle from '../bundles/localizationBundle.json';

import {
    combineReducers,
    Reducer
} from 'redux';

enum LocalizeActionTypes {
    GET_LOCALIZATION_BUNDLE = '@@localize/GET_LOCALIZATION_BUNDLE'
}

const getLocalizationBundle: Reducer<{}> = (
        state: any = '',
        action: ILocalizeReduxAction): any => {
    switch (action.type) {
        case LocalizeActionTypes.GET_LOCALIZATION_BUNDLE:
            return bundle;
        default:
            return bundle;
    }
};

const localizeReducer: Reducer = combineReducers({
    localizationBundle: getLocalizationBundle
});

interface ILocalizeReduxAction {
    //tslint:disable-next-line:no-reserved-keywords
    type: string;
}

// tslint:disable-next-line:export-name
export {
    localizeReducer,
    LocalizeActionTypes,
    ILocalizeReduxAction,
    getLocalizationBundle
};
